import './form.scss';

import { useState } from 'react';

function Form(props) {
  let [showPostTextArea, setShowPostTextArea] = useState(false);
  let [method, setmethod] = useState('get');
  let [url, seturl] = useState('https://pokeapi.co/api/v2/pokemon');
  let [requestBody, setrequestBody] = useState();
  function save() {
    let saved = getData();

    if (history.length === 0 || history.length === 1) {
      for (let i in saved) {
        history.unshift(saved[i]);
      }
    }
    localStorage.setItem('history', JSON.stringify(history));
  }
  function getData() {
    let information = localStorage.getItem('history');
    if (information) {
      information = JSON.parse(information);
      return information;
    } else {
      return [];
    }
  }

  let history = [];
  function handleSubmit(e) {
    e.preventDefault();
    const formData = {
      method: method,
      url: url,
    };
    props.handleApiCall(formData, requestBody);
  }

  function handlePostTextArea(e) {
    setShowPostTextArea(!showPostTextArea);
    setmethod(e.target.id);
  }

  function setMethod(e) {
    setmethod(e.target.id);
  }

  function handleUrl(e) {
    seturl(e.target.value);
  }

  function handleRequestBody(e) {
    setrequestBody(e.target.value);
  }

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          <span>URL: </span>
          <input name="url" type="text" onChange={handleUrl} />
          <button type="submit" data-testid="submit">
            GO!
          </button>
        </label>
        <label className="methods">
          <button type="button" id="hide"></button>
          <button className="butt" type="button" id="get" onClick={setMethod}>
            GET
          </button>
          <button
            className="butt"
            type="button"
            id="post"
            onClick={handlePostTextArea}
          >
            POST
          </button>
          <button
            className="butt"
            type="button"
            id="put"
            onClick={handlePostTextArea}
          >
            PUT
          </button>
          <button
            className="butt"
            type="button"
            id="delete"
            onClick={setMethod}
          >
            DELETE
          </button>
        </label>
        {showPostTextArea && (
          <textarea
            name="postAndPut"
            rows="10"
            cols="35"
            onChange={handleRequestBody}
          />
        )}
      </form>
    </>
  );
}

export default Form;
