import React from 'react';
import './app.scss';
import axios from 'axios';
import { useState, useEffect, useReducer } from 'react';
import Header from './components/header/header';
import Footer from './components/footer/footer';
import Form from './components/form/form';
import History from './components/history/history';
import Results from './components/results/results';

const array = [];

function historyReducer(history = array, action) {
  const { type, payload } = action;
  switch (type) {
    case 'AddToHistory':
      console.log(history);
      history = [...history, payload];
      return history;
    default:
      return history;
  }
}
function addToHistory(url, method, result) {
  return {
    type: 'AddToHistory',
    payload: {
      url,
      method,
      result,
    },
  };
}
function App() {
  const [data, setData] = useState(null);
  const [requestParams, setRequestParams] = useState({});
  const [state, dispatch] = useReducer(historyReducer, initialState);
  const [loading, setLoading] = useState(false);

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(async () => {
    if (requestParams.url) {
      const data = await axios[requestParams.method](requestParams.url);
      setData(data);
      setLoading(false);
    }
  }, [requestParams]);

  async function callApi(formData) {
    setLoading(true);
    if (formData.url) {
      setRequestParams(formData);
      dispatch(historyAction(formData));
    } else {
      const data = {
        count: 2,
        method: formData.method,
        results: [
          { name: 'fake thing 1', url: 'http://fakethings.com/1' },
          { name: 'fake thing 2', url: 'http://fakethings.com/2' },
        ],
      };
      setData({ data });
      setRequestParams(formData);
      dispatch(historyAction(formData));
      setLoading(false);
    }
  }

  return (
    <React.Fragment>
      <Header />
      <div>Request Method: {requestParams.method?.toUpperCase()}</div>
      <div>URL: {requestParams.url}</div>
      <Form handleApiCall={callApi} />
      {state.history.length ? <History history={state.history} /> : null}
      {loading ? <BeatLoader loading /> : data && <Results data={data} />}
      <Footer />
    </React.Fragment>
  );
}

export default App;
